import React, { useState, useEffect } from "react"
import axios from 'axios'
import './loglist.css'

const API_URL = 'http://localhost:5000'

const api = axios.create({
   baseURL: API_URL,
   headers: {
      'Content-Type': 'application/json',
   },
})

const apiService = {
   getLogs: async () => {
      try {
         const response = await api.get('/logs')
         return response.data.logs
      } catch (error) {
         console.error('Error fetching logs:', error)
         throw new Error('Failed to fetch logs')
      }
   },

   filterLogs: async (startDate, endDate, keyword = '') => {
      try {
         let url = `/logs/filter?start_date=${startDate}&end_date=${endDate}`

         if (keyword) {
            url += `&keyword=${keyword}`
         }

         const response = await api.get(url)
         return response.data.logs
      } catch (error) {
         console.error('Error filtering logs:', error)
         throw new Error('Failed to filter logs')
      }
   }
}

const LogList = () => {
   const [logs, setLogs] = useState([])
   const [filteredLogs, setFilteredLogs] = useState([])
   const [startDate, setStartDate] = useState('')
   const [endDate, setEndDate] = useState('')
   const [keyword, setKeyword] = useState('')

   useEffect(() => {
      fetchLogs()
   }, [])

   const fetchLogs = async () => {
      try{
         const logsData = await apiService.getLogs()
         setLogs(logsData)
      } catch (error) {
         console.error('Error fetching logs:', error)
      }
   }

   const handleFilter = async () => {
      try {
         const regex = /^\d{4}-\d{2}-\d{2}$/

         if (!startDate.match(regex) || !endDate.match(regex)) {
            console.error('Datas inválidas - devem estar no formato YYYY-MM-DD')
            return
         }

         if (!startDate || !endDate) {
            console.error('Por favor, preencha ambas as datas para filtrar.')
            return
         }

         const formattedStartDate = startDate !== '' ? new Date(startDate).toLocaleDateString('pt-BR').split('/').reverse().join('-') : ''
         const formattedEndDate = endDate !== '' ? new Date(endDate).toLocaleDateString('pt-BR').split('/').reverse().join('-') : ''

         const logsData = await apiService.filterLogs(formattedStartDate, formattedEndDate, keyword)
         setFilteredLogs(logsData)
         console.log(logsData)
      } catch (error) {
         console.error("Error filtering logs", error)
      }
   }

   const isValidTime = (time) => {
      const timeRegex = /^([01]\d|2[0-3]):([0-5]\d):([0-5]\d)$/
      return timeRegex.test(time)
   }

   const formatTime = (time) => {
      const [hours, minutes, seconds] = time.split(':')
      return `${hours}:${minutes}:${seconds}`
   }

   return (
      <div className="log-list">
         <h1>Log List</h1>
         <div className="filters">
            <h2>Filters:</h2>
            <input
               className="filter-input"
               type="text"
               placeholder="Start date"
               value={startDate}
               onChange={(e) => setStartDate(e.target.value)}
            />
            <input
               className="filter-input"
               type="text"
               placeholder="End date"
               value={endDate}
               onChange={(e) => setEndDate(e.target.value)}
            />
            <input  
               className="filter-input"
               type="text"
               placeholder="Keyword"
               value={keyword}
               onChange={(e) => setKeyword(e.target.value)}
            />
            <button className="apply-button" onClick={handleFilter}>Apply</button>
         </div>
         <div className="log-cards">
            {(filteredLogs.length > 0 ? filteredLogs : logs).map((log) => (
               <div key={log._id} className="log-card">
               <p><b>ID:</b> {log._id}</p>
               <p><b>IP:</b> {log.ip_address}</p>
               <p><b>Date:</b> {new Date(log.date).toLocaleDateString('pt-BR')}</p>
               <p><b>Time:</b> {isValidTime(log.time) ? formatTime(log.time) : 'Invalid Time'}</p>
               <p><b>Username:</b> {log.username}</p>
               <p><b>Version:</b> {log.version}</p>
               <p><b>Document:</b> {log.document}</p>
               <p><b>Description:</b> {log.description}</p>
               </div>
            ))}
         </div>
      </div>
   )
}

export default LogList
