import React, { useState, useEffect } from "react"
import apiService from "../services/api"
import LogFilter from "./LogFilter"
import './loglist.css'

const LogList = () => {
   const [logs, setLogs] = useState([])
   const [filteredLogs, setFilteredLogs] = useState([])

   useEffect(() => {
      fetchLogs()
   }, [])

   const fetchLogs = async () => {
      try{
         const logsData = await apiService.getLogs()
         setLogs(logsData)
      } catch (error) {
         console.error('Error fetching logs:', error)
      }
   }

   const isValidTime = (time) => {
      const timeRegex = /^([01]\d|2[0-3]):([0-5]\d):([0-5]\d)$/
      return timeRegex.test(time)
   }

   const formatTime = (time) => {
      const [hours, minutes, seconds] = time.split(':')
      return `${hours}:${minutes}:${seconds}`
   }

   return (
      <div className="log-list">
         <h1>Log List</h1>
         <LogFilter handleFilter={handleFilter} />
         <div className="log-cards">
            {(filteredLogs.length > 0 ? filteredLogs : logs).map((log) => (
               <div key={log._id} className="log-card">
               <p><b>ID:</b> {log._id}</p>
               <p><b>IP:</b> {log.ip_address}</p>
               <p><b>Date:</b> {new Date(log.date).toLocaleDateString('pt-BR')}</p>
               <p><b>Time:</b> {isValidTime(log.time) ? formatTime(log.time) : 'Invalid Time'}</p>
               <p><b>Username:</b> {log.username}</p>
               <p><b>Version:</b> {log.version}</p>
               <p><b>Document:</b> {log.document}</p>
               <p><b>Description:</b> {log.description}</p>
               </div>
            ))}
         </div>
      </div>
   )
}

export default LogList
